import { useEffect, useState } from "react";
import "./App.css";

function App() {
  const [idea, setIdea] = useState("");
  const [output, setOutput] = useState("");
  const [displayText, setDisplayText] = useState("");
  const [platform, setPlatform] = useState("Instagram");
  const [action, setAction] = useState("generate");
  const [dark, setDark] = useState(true);
  const [loading, setLoading] = useState(false);

  // NEW STATES (LOGIN / SIGNUP)
  const [showLogin, setShowLogin] = useState(false);
  const [showSignup, setShowSignup] = useState(false);

  // Typing animation
  useEffect(() => {
    let i = 0;
    setDisplayText("");
    if (!output) return;

    const interval = setInterval(() => {
      setDisplayText((prev) => prev + output.charAt(i));
      i++;
      if (i >= output.length) clearInterval(interval);
    }, 15);

    return () => clearInterval(interval);
  }, [output]);

  const generate = async () => {
    setLoading(true);
    setOutput("");

    const res = await fetch("http://127.0.0.1:8000/content", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ idea, platform, action })
    });

    const data = await res.json();
    setOutput(data.result || "No response");
    setLoading(false);
  };

  const copyText = () => {
    navigator.clipboard.writeText(output);
    alert("Copied to clipboard ‚úÖ");
  };

  const downloadText = () => {
    const blob = new Blob([output], { type: "text/plain" });
    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.download = "kirowrite.txt";
    link.click();
  };

  return (
    <div className={dark ? "app dark" : "app light"}>
      {/* NAVBAR */}
      <div className="navbar">
        <h2>KiroWrite ‚ú®</h2>
        <div>
          <button onClick={() => setDark(!dark)}>
            {dark ? "‚òÄÔ∏è Light" : "üåô Dark"}
          </button>
          <button className="auth" onClick={() => setShowLogin(true)}>
            Login
          </button>
          <button
            className="auth outline"
            onClick={() => setShowSignup(true)}
          >
            Sign Up
          </button>
        </div>
      </div>

      {/* MAIN CARD */}
      <div className="card">
        <h1>AI Content Generator</h1>
        <p>Create powerful content in seconds üöÄ</p>

        <textarea
          placeholder="Enter your idea..."
          value={idea}
          onChange={(e) => setIdea(e.target.value)}
        />

        <div className="row">
          <select value={platform} onChange={(e) => setPlatform(e.target.value)}>
            <option>Instagram</option>
            <option>LinkedIn</option>
            <option>Blog</option>
          </select>

          <select value={action} onChange={(e) => setAction(e.target.value)}>
            <option value="generate">Generate</option>
            <option value="rewrite">Rewrite</option>
            <option value="summarize">Summarize</option>
          </select>
        </div>

        <button className="generate" onClick={generate} disabled={loading}>
          {loading ? "Generating..." : "Generate ‚ú®"}
        </button>

        {displayText && (
          <div className="output">
            <pre>{displayText}</pre>

            <div className="output-actions">
              <button onClick={copyText}>üìã Copy</button>
              <button onClick={downloadText}>‚¨áÔ∏è Download</button>
            </div>
          </div>
        )}
      </div>

      {/* LOGIN MODAL */}
      {showLogin && (
        <div className="auth-page">
          <div className="auth-card">
            <h2>Login</h2>
            <input placeholder="Email" />
            <input type="password" placeholder="Password" />
            <button className="generate">Login</button>

            <p>
              Don‚Äôt have an account?{" "}
              <span
                className="link"
                onClick={() => {
                  setShowLogin(false);
                  setShowSignup(true);
                }}
              >
                Sign Up
              </span>
            </p>

            <button onClick={() => setShowLogin(false)}>Close</button>
          </div>
        </div>
      )}

      {/* SIGNUP MODAL */}
      {showSignup && (
        <div className="auth-page">
          <div className="auth-card">
            <h2>Sign Up</h2>
            <input placeholder="Email" />
            <input type="password" placeholder="Password" />
            <button className="generate">Create Account</button>

            <p>
              Already have an account?{" "}
              <span
                className="link"
                onClick={() => {
                  setShowSignup(false);
                  setShowLogin(true);
                }}
              >
                Login
              </span>
            </p>

            <button onClick={() => setShowSignup(false)}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
