import { useEffect, useState } from "react";
import "./App.css";

function App() {
  const [idea, setIdea] = useState("");
  const [output, setOutput] = useState("");
  const [displayText, setDisplayText] = useState("");
  const [platform, setPlatform] = useState("Instagram");
  const [action, setAction] = useState("generate");
  const [dark, setDark] = useState(true);
  const [loading, setLoading] = useState(false);

  // AUTH STATES
  const [showLogin, setShowLogin] = useState(false);
  const [user, setUser] = useState(null);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  // üîπ USER SESSION (LOAD ON REFRESH)
  useEffect(() => {
    const savedUser = localStorage.getItem("user");
    if (savedUser) {
      setUser(JSON.parse(savedUser));
    }
  }, []);

  // Typing animation
  useEffect(() => {
    let i = 0;
    setDisplayText("");
    if (!output) return;

    const interval = setInterval(() => {
      setDisplayText((prev) => prev + output.charAt(i));
      i++;
      if (i >= output.length) clearInterval(interval);
    }, 15);

    return () => clearInterval(interval);
  }, [output]);

  // CONTENT GENERATION
  const generate = async () => {
    setLoading(true);
    setOutput("");

    const res = await fetch("http://127.0.0.1:8000/content", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ idea, platform, action })
    });

    const data = await res.json();
    setOutput(data.result || "No response");
    setLoading(false);
  };

  // LOGIN (CONNECTS TO BACKEND)
  const login = async () => {
    const res = await fetch("http://127.0.0.1:8000/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ email, password })
    });

    const data = await res.json();

    if (data.success) {
      localStorage.setItem("user", JSON.stringify(data));
      setUser(data);
      setShowLogin(false);
    }
  };

  const logout = () => {
    localStorage.removeItem("user");
    setUser(null);
  };

  return (
    <div className={dark ? "app dark" : "app light"}>
      {/* NAVBAR */}
      <div className="navbar">
        <h2>KiroWrite ‚ú®</h2>
        <div>
          <button onClick={() => setDark(!dark)}>
            {dark ? "‚òÄÔ∏è Light" : "üåô Dark"}
          </button>

          {user ? (
            <>
              <span className="username">üë§ {user.username}</span>
              <button onClick={logout}>Logout</button>
            </>
          ) : (
            <button className="auth" onClick={() => setShowLogin(true)}>
              Login
            </button>
          )}
        </div>
      </div>

      {/* MAIN CARD */}
      <div className="card">
        <h1>AI Content Generator</h1>
        <p>Create powerful content in seconds üöÄ</p>

        <textarea
          placeholder="Enter your idea..."
          value={idea}
          onChange={(e) => setIdea(e.target.value)}
        />

        <div className="row">
          <select value={platform} onChange={(e) => setPlatform(e.target.value)}>
            <option>Instagram</option>
            <option>LinkedIn</option>
            <option>Blog</option>
          </select>

          <select value={action} onChange={(e) => setAction(e.target.value)}>
            <option value="generate">Generate</option>
            <option value="rewrite">Rewrite</option>
            <option value="summarize">Summarize</option>
          </select>
        </div>

        <button className="generate" onClick={generate} disabled={loading}>
          {loading ? "Generating..." : "Generate ‚ú®"}
        </button>

        {displayText && (
          <div className="output">
            <pre>{displayText}</pre>
          </div>
        )}
      </div>

      {/* LOGIN MODAL */}
      {showLogin && (
        <div className="auth-page">
          <div className="auth-card">
            <h2>Login</h2>

            <input
              placeholder="Email"
              onChange={(e) => setEmail(e.target.value)}
            />

            <input
              type="password"
              placeholder="Password"
              onChange={(e) => setPassword(e.target.value)}
            />

            <button className="generate" onClick={login}>
              Login
            </button>

            <button onClick={() => setShowLogin(false)}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
