import { useState } from "react";
import "./App.css";

function App() {
  const [idea, setIdea] = useState("");
  const [output, setOutput] = useState("");
  const [platform, setPlatform] = useState("Instagram");
  const [action, setAction] = useState("generate");
  const [loading, setLoading] = useState(false);

  const typeEffect = (text) => {
    setOutput("");
    let i = 0;
    const interval = setInterval(() => {
      setOutput((prev) => prev + text.charAt(i));
      i++;
      if (i >= text.length) clearInterval(interval);
    }, 15);
  };

  const generate = async () => {
    if (!idea.trim()) return alert("Please enter an idea");

    setLoading(true);
    setOutput("");

    try {
      const res = await fetch("http://127.0.0.1:8000/content", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          idea,
          platform,
          action,
        }),
      });

      const data = await res.json();
      typeEffect(data.result || "No response received");
    } catch (err) {
      setOutput("‚ùå Error connecting to backend");
    }

    setLoading(false);
  };

  return (
    <div className="app-bg">
      <div className="card">
        <h1>KiroWrite ‚ú®</h1>
        <p className="subtitle">
          AI-powered content generator for creators & professionals
        </p>

        {/* Platform */}
        <select value={platform} onChange={(e) => setPlatform(e.target.value)}>
          <option>Instagram</option>
          <option>LinkedIn</option>
          <option>Blog</option>
        </select>

        {/* Action */}
        <select value={action} onChange={(e) => setAction(e.target.value)}>
          <option value="generate">Generate</option>
          <option value="rewrite">Rewrite</option>
          <option value="summarize">Summarize</option>
        </select>

        {/* Input */}
        <textarea
          placeholder="üí° Enter your idea here..."
          value={idea}
          onChange={(e) => setIdea(e.target.value)}
        />

        {/* Button */}
        <button onClick={generate} disabled={loading}>
          {loading ? "‚ú® Generating..." : "üöÄ Generate Content"}
        </button>

        {/* Output */}
        {output && (
          <div className="output">
            <pre>{output}</pre>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
