import { useEffect, useState } from "react";
import "./App.css";

function App() {
  const [idea, setIdea] = useState("");
  const [platform, setPlatform] = useState("Instagram");
  const [output, setOutput] = useState("");
  const [displayText, setDisplayText] = useState("");
  const [loading, setLoading] = useState(false);

  const generate = async () => {
    if (!idea.trim()) return;

    setLoading(true);
    setOutput("");
    setDisplayText("");

    const res = await fetch("http://127.0.0.1:8000/content", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        idea,
        platform,
        action: "generate",
      }),
    });

    const data = await res.json();
    setOutput(data.result || "No response");
    setLoading(false);
  };

  // ðŸ”¥ Typing animation effect
  useEffect(() => {
    if (!output) return;

    let i = 0;
    const interval = setInterval(() => {
      setDisplayText((prev) => prev + output[i]);
      i++;
      if (i >= output.length) clearInterval(interval);
    }, 15);

    return () => clearInterval(interval);
  }, [output]);

  const copyText = () => {
    navigator.clipboard.writeText(output);
    alert("Copied to clipboard ðŸ“‹");
  };

  return (
    <div className="app">
      <div className="card">
        <h1 className="title">KiroWrite ðŸš€</h1>
        <p className="subtitle">AI-powered content generator</p>

        <textarea
          className="input"
          placeholder="Enter your content idea..."
          value={idea}
          onChange={(e) => setIdea(e.target.value)}
        />

        <div className="controls">
          <select
            className="select"
            value={platform}
            onChange={(e) => setPlatform(e.target.value)}
          >
            <option>Instagram</option>
            <option>LinkedIn</option>
            <option>Twitter</option>
          </select>

          <button className="btn" onClick={generate}>
            {loading ? "Generating..." : "Generate âœ¨"}
          </button>
        </div>

        {displayText && (
          <div className="output-box">
            <button className="copy-btn" onClick={copyText}>
              ðŸ“‹ Copy
            </button>
            <pre className="output">{displayText}</pre>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
