import { useState } from "react";
import "./App.css";

function App() {
  const [idea, setIdea] = useState("");
  const [output, setOutput] = useState("");
  const [loading, setLoading] = useState(false);

  const generate = async () => {
    setLoading(true);
    const res = await fetch("http://127.0.0.1:8000/content", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        idea,
        platform: "Instagram",
        action: "generate"
      })
    });

    const data = await res.json();
    setOutput(data.result || "No response");
    setLoading(false);
  };

  return (
    <div className="app">
      <div className="card">
        <h1 className="title">KiroWrite ðŸš€</h1>
        <p className="subtitle">
          AI-powered content generator for social media
        </p>

        <textarea
          rows="5"
          placeholder="âœ¨ Describe your content idea..."
          value={idea}
          onChange={(e) => setIdea(e.target.value)}
        />

        <button onClick={generate} disabled={loading || !idea}>
          {loading ? "Generating..." : "Generate Content"}
        </button>

        {output && (
          <div className="output">
            <h3>âœ¨ Generated Content</h3>
            <pre>{output}</pre>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
